<?phpnamespace Fixwa\TinyUuid;class TinyUuid{    /**     * Generates a Random "Mixed Case" string of the specified length consisting     * only of numbers and letters, e.g: ´crkeG´, ´zL6VY´, etc.     *     * The maximum string length is 128, while the minimum is 4.     *     * The "Salt" can be anything, it will still use a ´uniqid´ to add more "randomness".     *     * @param int $len     * @return false|string     */    public static function tinyUuid($len = 6)    {        $hex = md5("RANDOM_SALT_XD#@$" . uniqid("", true));        $pack = pack('H*', $hex); //Generate a binary string from the Salt.        $uid = base64_encode($pack);        $uid = preg_replace("/\W/", '', $uid);        if ($len < 4) {            $len = 4;        }        if ($len > 128) {            $len = 128;        }        while (strlen($uid) < $len) {            $uid = $uid . TinyUuid::tinyUuid(22);        }        return substr($uid, 0, $len);    }}